script:
frappe.ui.form.on('Job Card', {
	refresh(frm) {
		if(frm.doc.docstatus ==0){
    		add_quality_inspection(frm);
		}
	},
	before_submit:function(frm){
	    frappe.db.get_list('Quality Inspection', {
	filters: {
		reference_name: frm.doc.name,
        reference_type: "Job Card",
	},
	fields: ['count(name) as count'],
}).then(res => {
	if(res[0].count === 0){
	    frappe.validated=false;
	   frappe.msgprint("Quality inspection is required");
	}
});

	    $.each(frm.doc.time_logs,function(idx, row){
	        if(row.accepted_qty>row.completed_qty){
	            frappe.validate=false;
    		    frappe.throw(__("Time Logs <br> Row:{0} Accepted Qty should not be greater than Completed Qty.", [idx+1]));
	        }
	        });
	}

});

function add_quality_inspection(frm){
	frm.add_custom_button(__('Quality Inspection'), () => {
		frappe.call({
			method: 'manufacturer_customizations.manufacturer_customizations.doctype.job_card.job_card.make_quality_inspection',
			args: {
				'doc': frm.doc
			},
			async: false,
			callback: function(r){
			    if(r.message)
				frappe.set_route("Form", "Quality Inspection", r.message);
			}
		})
	}, __('Make'));
}

frappe.ui.form.on('Work Order Item', {
    length: function(frm,cdt,cdn){
        var d = locals[cdt][cdn];
        d.product = d.length*d.breadth
        refresh_field("required_items");

    },

    breadth: function(frm,cdt,cdn){
        var d = locals[cdt][cdn];
        d.product = d.length*d.breadth
        refresh_field("required_items");

    }
})

frappe.ui.form.on('Work Order', {
	refresh(frm) {
	   // if(frm.doc.docstatus==0){
	        if(frm.doc.status=="Not Started")
	        frappe.db.get_value('BOM', frm.doc.bom_no, 'inspection_required',(r) => {
	            if(r.inspection_required==1){
	                frm.add_custom_button(__('Quality Inspection'), function(){
	                    frappe.new_doc('Quality Inspection',true)
	                    frappe.route_options = {
	                        reference_doctype: "Quality Inspection",
	                        reference_type: "Work Order",
	                        reference_name: frm.doc.name,
	                        item_code: frm.doc.production_item,
	                        quantity_tested: frm.doc.final_qty
	                    }
	                });
	               // frappe.db.get_value('Job Card',frm.doc.work_order,)
	            }
	        });

	    frappe.call({
	        method: 'frappe.client.get_list',
	        args: {
	            doctype: 'Quality Inspection',
	            filters: [
	                ['reference_type', '=', 'Work Order'],
	                ['reference_name', '=', frm.doc.name]
	                ]
	        },
	        callback:function(e){
	            if((e.message.length)==0){
	                frm.add_custom_button(__('Quality Inspection'), function(){
	                    frappe.new_doc('Quality Inspection',true)
	                    frappe.route_options = {
	                        reference_doctype: "Quality Inspection",
	                        reference_type: "Work Order",
	                        reference_name: frm.doc.name,
	                        item_code: frm.doc.production_item,
	                        quantity_tested: frm.doc.final_qty

	                    }
	                })

	            frm.remove_custom_button('Finish');
	        }
	        else{

	        }
	        }

	    });
	    frappe.call({
	        method: 'frappe.client.get_list',
	        args: {
	            doctype: 'Quality Inspection',
	            filters: [
	                ['reference_type', '=', 'Work Order'],
	                ['reference_name', '=', frm.doc.name]
	                ]
	        },
	        callback:function(e){
	            if((e.message.length)==1){

	            frm.remove_custom_button('Quality Inspection');
	        }
	        else{

	        }
	        }

	    });
	    if(frm.doc.status=="In Process"){
	        frm.remove_custom_button('Finish');
	    }

	}
})

frappe.ui.form.on('BOM', {
	refresh(frm) {
		frm.set_query("item_code", "items", function(doc, cdt, cdn) {
			return {
				filters: {
					item_group:"Raw Material (BOM)"
				}
			};
		});
	},
	onload(frm){
	    frm.set_query("item_code", "items", function(doc, cdt, cdn) {
			return {
				filters: {
					item_group:"Raw Material (BOM)"
				}
			};
		});
	}

})

frappe.ui.form.on('BOM Item', {

})

frappe.ui.form.on('Quality Inspection', {
	set_default(frm) {
	    if(frm.doc.set_default == 1){
	        $.each(frm.doc.readings,function(idx, item){
	            if(item.specification == 'Hole Profile'){
	                item.mesured_observed = 'Hole profiles are good'
	            }
	            else if(item.specification == 'Bow, Twist & Wrap'){
	                item.mesured_observed = 'Within acceptable limit'
	            }
	            else if(item.specification == 'Solder Mask - Hardness'){
	                item.mesured_observed = 'Passed'
	            }
	            else if(item.specification == 'Min Trace width / space'){
	                item.mesured_observed = 'Measured and Found Ok'
	            }
	            else if(item.specification == 'Solderability HASL/ENiG/Hard Gold'){
	                item.mesured_observed = 'Good'
	            }
	            else if(item.specification == 'General Aesthatic'){
	                item.mesured_observed = 'Free from scratches and dust'
	            }
	            else if(item.specification == 'Electrical Test'){
	                item.mesured_observed = 'Passed'
	            }
	        });
	        refresh_field("readings");
	    }
	},
	refresh(frm,cdt,cdn){
	    frm.set_query("parameter_value", "readings", function(frm, cdt, cdn) {
	        var child = locals[cdt][cdn];
            return {
                    query: "manufacturer_customizations.manufacturer_customizations.doctype.quality_inspection.quality_inspection.get_parameter_value_filters",
                    filters:{
                        "parameter":child.specification
                    },

            };
        });
	}
})

frappe.ui.form.on('Quality Inspection Reading', {
    refresh(frm){

    },
	parameter_value(frm,cdt,cdn) {
		var row = locals[cdt][cdn];
		row.mesured_observed = row.parameter_value;
		refresh_field("readings");
	}
})
